import UIKit

protocol CharacterDetailsBusinessLogic {
    func showDetails()
}

protocol CharacterDetailsDataStore {
    var characterDetail: Result? { get set }
}

class CharacterDetailsInteractor: CharacterDetailsBusinessLogic, CharacterDetailsDataStore {

    var characterDetail: Result?
    
    var presenter: CharacterDetailsPresentationLogic?
    var worker: CharacterDetailsWorker?
    
    func showDetails() {
        if let characterDetail = characterDetail {
            let response = CharacterDetails.ShowCharacterDetails.Response(characterDetail: characterDetail)
            presenter?.presentCharacterDetail(response: response)
        }
    }
}
