import UIKit

@objc protocol CharacterListRoutingLogic {
    func routeToCharacterDetail(indexPath: IndexPath)
}

protocol CharacterListDataPassing {
  var dataStore: CharacterListDataStore? { get }
}

class CharacterListRouter: NSObject, CharacterListRoutingLogic, CharacterListDataPassing {
    
    weak var viewController: CharacterListViewController?
    var dataStore: CharacterListDataStore?
    
    func routeToCharacterDetail(indexPath: IndexPath) {
        let vc = CharacterDetailsViewController()
        var destinationDS = vc.router!.dataStore!
        passDataToSomewhere(source: dataStore!, destination: &destinationDS, indexPath: indexPath)
        viewController?.navigationController?.pushViewController(vc, animated: true)
    }
    
    // MARK: Passing data
    func passDataToSomewhere(source: CharacterListDataStore, destination: inout CharacterDetailsDataStore, indexPath: IndexPath){
        let result = source.list?.data.results[indexPath.row]
        destination.characterDetail = result
    }
}
